public class EditorGUILayout {
    public void BeginBuildTargetSelectionGrouping(); // Begin a build target grouping and get the selected BuildTargetGroup back.
    public void BeginFadeGroup(); // Begins a group that can be be hidden/shown and the transition will be animated.
    public void BeginFoldoutHeaderGroup(); // Make a label with a foldout arrow to the left of it.
    public void BeginHorizontal(); // Begin a horizontal group and get its rect back.
    public void BeginScrollView(); // Begin an automatically laid out scrollview.
    public void BeginToggleGroup(); // Begin a vertical group with a toggle to enable or disable all the controls withi
    public void BeginVertical(); // Begin a vertical group and get its rect back.
    public void BoundsField(); // Make Center & Extents field for entering a Bounds.
    public void BoundsIntField(); // Make Position & Size field for entering a BoundsInt.
    public void ColorField(); // Make a field for selecting a Color.
    public void CurveField(); // Make a field for editing an AnimationCurve.
    public void DelayedDoubleField(); // Make a delayed text field for entering doubles.
    public void DelayedFloatField(); // Make a delayed text field for entering floats.
    public void DelayedIntField(); // Make a delayed text field for entering integers.
    public void DelayedTextField(); // Make a delayed text field.
}
