public class JsonUtility {
    public void FromJson(); // Create an object from its JSON representation.
    public void FromJsonOverwrite(); // Overwrite data in an object by reading from its JSON representation.
    public void ToJson(); // Generate a JSON representation of the public fields of an object.
}
