    public var alphaCutoff;
    public var backSortingLayerID;
    public var backSortingOrder;
    public var frontSortingLayerID;
    public var frontSortingOrder;
    public var isCustomRangeActive;
    public var maskSource;
    public var sprite;
    public var spriteSortPoint;
