// Pseudo-code representation of AnimatorControllerParameter
public class AnimatorControllerParameter {
    public var defaultBool; // The default bool value for the parameter.
    public var defaultFloat; // The default float value for the parameter.
    public var defaultInt; // The default int value for the parameter.
    public var name; // The name of the parameter.
    public var nameHash; // Returns the hash of the parameter based on its name.
    public var type; // The type of the parameter.
}
