// Pseudo-code representation of Avatar
public class Avatar {
    public var humanDescription; // Returns the HumanDescription used to create this Avatar.
    public var isHuman; // Return true if this avatar is a valid human avatar.
    public var isValid; // Return true if this avatar is a valid mecanim avatar. It can be a generic avatar or a human avatar.
    public void GetInstanceID(); // Gets the instance ID of the object.
    public void ToString(); // Returns the name of the object.
}
