public class AssetBundle {
    public var isStreamedSceneAssetBundle; // Return true if the AssetBundle contains Unity Scene files
    public void Contains(); // Check if an AssetBundle contains a specific object.
    public void GetAllAssetNames(); // Return all Asset names in the AssetBundle.
    public void GetAllScenePaths(); // Return all the names of Scenes in the AssetBundle.
    public void LoadAllAssets(); // Loads all Assets contained in the AssetBundle synchronously.
    public void LoadAllAssetsAsync(); // Loads all Assets contained in the AssetBundle asynchronously.
    public void LoadAsset(); // Synchronously loads an Asset from the AssetBundle.
    public void LoadAssetAsync(); // Asynchronously loads an Asset from the bundle.
    public void LoadAssetWithSubAssets(); // Loads Asset and sub Assets from the AssetBundle synchronously.
    public void LoadAssetWithSubAssetsAsync(); // Loads Asset and sub Assets from the AssetBundle asynchronously.
    public void Unload(); // Unloads an AssetBundle freeing its data.
    public void UnloadAsync(); // Unloads assets in the bundle.
    public void GetAllLoadedAssetBundles(); // Get an enumeration of all the currently loaded AssetBundles.
    public void LoadFromFile(); // Synchronously loads an AssetBundle from a file on disk.
    public void LoadFromFileAsync(); // Asynchronously loads an AssetBundle from a file on disk.
    public void LoadFromMemory(); // Synchronously load an AssetBundle from a memory region.
}
